import React, { Component } from 'react';
import { Container } from './styles';

export default class FallingBlock extends Component {
    constructor(props) {
        super(props);
        this.fall = this.fall.bind(this);
    }
    fall(e) {
        e.preventDefault();
        setInterval( () => {
            if (this.style.bottom !== 0) {
                this.style.top += 1;
            }

        } ,1000);
    }
    render() {
        return(
            <Container onClick={this.fall}/>
        );
    }
}
